import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Carousel } from "react-bootstrap";
import { render } from "@testing-library/react";

function ControlledCarousel() {
  const [index, setIndex] = useState(0);
  const [images, setImage] = useState([
    "https://images.unsplash.com/photo-1449034446853-66c86144b0ad?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2100&q=80",
    "https://images.unsplash.com/photo-1470341223622-1019832be824?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2288&q=80",
    "https://images.unsplash.com/photo-1448630360428-65456885c650?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2094&q=80",
    "https://images.unsplash.com/photo-1534161308652-fdfcf10f62c4?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2174&q=80",
  ]);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  return (
    <Carousel
      fade
      control={false}
      activeIndex={index}
      onSelect={handleSelect}
      interval={null}
    >
      <Carousel.Item style={{ height: "400px" }}>
        <img
          className="d-block w-100"
          src={require("../asset/Carousel/image1.jpg")}
          alt="First slide"
          style={{ height: "400px" }}
        />

        <Carousel.Caption>
          <h3>Pothos</h3>
          <p>Beautiful cascading foilage that grows with their surrounding</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item style={{ height: "400px" }}>
        <img
          className="d-block w-100"
          src={require("../asset/Carousel/image2.jpg")}
          alt="Second slide"
          style={{ height: "400px" }}
        />

        <Carousel.Caption>
          <h3>Cactus</h3>
          <p>Gentle beauty with low maintenance</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item style={{ height: "400px" }}>
        <img
          className="d-block w-100"
          src={require("../asset/Carousel/image3.jpg")}
          alt="Third slide"
          style={{ height: "400px" }}
        />

        <Carousel.Caption>
          <h3>Philodendron</h3>
          <p>Low light topical gorgeous with beautiful foilage</p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>
  );
}

export default ControlledCarousel;
